import React from 'react';
import * as pubsub from './pubsub';

export class Component extends React.Component {
    on(msg, fn) {
        pubsub.subscribe(msg, this, fn);
    }

    emit(msg, ...args) {
        pubsub.publish(msg, ...args);
    }

    componentWillUnmount() {
        pubsub.unsubscribe(this);
    }

    render() {
        return null;
    }

}
