let _conf = {};


function init(obj) {
    _conf = Object.assign({}, obj);
}

function get(key) {
    let val = _conf;
    for (let p of key.split('.')) {
        if (!(p in val))
            return null;
        val = val[p];
    }
    return val;
}


function str(key) {
    let v = get(key);
    return v === null ? null : String(v);
}

function number(key) {
    let v = get(key);
    return v === null ? null : Number(v);
}

function object(key) {
    let v = get(key);
    return v === null ? null : v;
}

export default { init, str, number, object };