let _conf = {};


export function init(obj) {
    _conf = Object.assign({}, obj);
}

function get(key: string) {
    let val = _conf;
    for (let p of key.split('.')) {
        if (!val)
            break;
        val = val[p];
    }
    return val;
}


export function str(key: string) : string|null {
    let v = get(key);
    return v ? String(v) : null;
}