/// XHR utility

import axios from 'axios';


async function get(url, params) {
    let res = await axios.get(url, {params});
    if (res.status === 200)
        return res.data;
    return null;


}

async function post(url, params) {
    let res = await axios.post(url, params);
    if (res.status === 200)
        return res.data;
    return null;

}

export default {get, post};
