/**
 * @module app
 *
 * @desc
 *
 * Core app framework and utilities.
 *
 */

import config from './config';
import core from './core';
import http from './http';

import TMap from '../map-util/tmap';

let _map = null;

function map() {
    if (_map)
        return _map;

    _map = new TMap();
    if (window)
        window._debugMap = _map;

    return _map;
}



export default {
    /**
     * @returns TMap
     *
     * @desc
     *
     * The global map object.
     */
    map,

    /**
     *
     * @desc
     *
     * Proivides access to the global config object.
     *
     */
    config,

    /**
     *
     * @desc
     *
     * HTTP utilities.
     *
     */
    http,

    ...core
};