import React from 'react';
import FontIcon from 'material-ui/FontIcon';
require('../../icons/gbdicons.font');

export default class GbdIcons extends React.Component {
    render() {
        let p = {
            className: 'material-icons gbdicons-' + this.props.icon,
            style: { fontFamily : 'gbdicons'},
        };

        if (this.props.small) {
            p.style.fontSize = 20;
        }

        if (this.props.color) {
            p.style.color = this.props.color;
        }

        return <FontIcon {...p}/>;
    }
}
