import React from 'react';

import app from '../app';

class ToolbarGroup extends React.Component {
    render() {
        let style = app.theme(
            'gwc.ui.toolbar.group'
            + (this.props.visible ? 'Visible' : ''));

        return (
            <div style={style}>{this.props.children}</div>
        );
    }
}

export default ToolbarGroup;

